//environment.peregrine_api_client_id = argv.peregrine_api_client_id
//environment.peregrine_api_client_secret = argv.peregrine_api_client_secret
import _yargs from 'yargs';
import { hideBin } from 'yargs/helpers';
const yargs = _yargs(hideBin(process.argv));
(async () => {
    const argv = await yargs
        .option('environment', { type: 'string', require: true })
        .option('peregrine_api_key', { type: 'string', require: true })
        .option('peregrine_api_audience', { type: 'string', require: true })
        .option('peregrine_api_authority', { type: 'string', require: true })
        .option('peregrine_api_client_id', { type: 'string', require: true })
        .option('peregrine_api_client_secret', { type: 'string', require: true })
        //.alias('f', 'filename')
        //.alias('c', 'content')
        .argv;
    //fs.writeFile(argv.filename, '' + argv.content, error => {
    //  if (error) throw error;
    //  console.log(`File ${argv.filename} saved.`);
    //});
    environment.environment = argv.environment;
    environment.peregrine_api_key = argv.peregrine_api_key;
    environment.peregrine_api_audience = argv.peregrine_api_audience;
    environment.peregrine_api_authority = argv.peregrine_api_authority;
    environment.peregrine_api_client_id = argv.peregrine_api_client_id;
    environment.peregrine_api_client_secret = argv.peregrine_api_client_secret;
})();
console.log('env=', environment.peregrine_api_key);
